import { useState } from "react";
import Expenses from "./components/Expenses/Expenses.js";
import NewExpense from "./components/NewExpense/NewExpense.js";
import ExpensesFilter from "./components/Expenses/ExpensesFilter.js";
function App() {
  const expenses = [
    {
      id: "e1",
      title: "Toilet Paper",
      amount: 94.12,
      date: new Date(2020, 7, 14),
    },
    { id: "e2", title: "New TV", amount: 799.49, date: new Date(2021, 2, 12) },
    {
      id: "e3",
      title: "Car Insurance",
      amount: 294.67,
      date: new Date(2021, 2, 28),
    },
    {
      id: "e4",
      title: "New Desk (Wooden)",
      amount: 450,
      date: new Date(2021, 5, 12),
    },
  ];
  const [expenseState, setExpenseState] = useState(expenses);
  function addExpenseHandler(expense) {
    setExpenseState(expenseState.concat(expense));
  }
  const [year, setYear] = useState("2022");
  function addFilterYearHandler(filterYear) {
    setYear(filterYear);
  }
  return (
    <div>
      <NewExpense onAddExpense={addExpenseHandler} />
      <ExpensesFilter onFilterYear={addFilterYearHandler} />
      <Expenses items={expenseState} year={year}></Expenses>
    </div>
  );
}

export default App;
