import { useState } from "react";
import "./Expenses.css";
import ExpenseItem from "./ExpenseItem";
import Card from "../UI/Card";
import ExpensesFilter from "./ExpensesFilter";

export default function Expenses(props) {
  const items = props.items;
  const [year, setYear] = useState("2022");
  function addFilterYearHandler(filteredYear) {
    setYear(filteredYear);
  }
  const filteredExpenses = items.filter(
    (ele) => ele.date.getFullYear().toString() === year
  ); //Vi sao ko phai la ===? Vi getFullYear tra ve 1 object

  let expensesContent = <p>No expenses found.</p>;
  if (filteredExpenses.length > 0) {
    expensesContent = filteredExpenses.map((element) => {
      return (
        <ExpenseItem
          key={element.id}
          title={element.title}
          date={element.date}
          amount={element.amount}
        ></ExpenseItem>
      );
    });
  }
  return (
    <div>
      <Card className="expenses">
        <ExpensesFilter
          onFilterYear={addFilterYearHandler}
          selectedYear={year}
        />
        {expensesContent}
      </Card>
    </div>
  );
}
