import "./NewExpense.css";
import ExpenseForm from "./ExpenseForm";

export default function NewExpense(props) {
  function saveExpenseDataHandler(enteredExpenseData) {
    const expenseData = {
      ...enteredExpenseData,
      id: Math.random().toString(),
    };
    console.log("Tai component New expense", expenseData);
    props.onAddExpense(expenseData);
  }
  let isAddExpense = false;
  function startAddNewExpense() {
    isAddExpense = isAddExpense === false ? true : false;
    console.log(isAddExpense);
  }
  return (
    <div className="new-expense">
      <button onClick={startAddNewExpense}>Add New Expense</button>
      {isAddExpense && (
        <ExpenseForm onSaveExpenseData={saveExpenseDataHandler} />
      )}
    </div>
  );
}
